import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './Navbar';
import Dashboard from './Frontend/Dashboard';
import Task from './Frontend/Task';
import TaskList from './Frontend/TaskList';
import Login from './Frontend/Login';
import Signup from './Frontend/Signup';
import { UserProvider } from './Frontend/UserContext';

const App = () => {
  // Check if the user is logged in
 // Assuming token is stored in localStorage after login

  return (
    <UserProvider>
      <Router>
        {/* Conditionally render the Navbar only if the user is authenticated */}
         <Navbar />

        <Routes>
          {/* Public Routes */}
          <Route path="/" element={<Login />} />
          <Route path="/signup" element={<Signup />} />

          {/* Private Routes */}


              <Route path="/dashboard" element={<Dashboard />} />
              <Route path="/task" element={<Task />} />
              <Route path="/tasklist" element={<TaskList />} />




            // Redirect to Login if the user is not authenticated


        </Routes>
      </Router>
    </UserProvider>
  );
};

export default App;
