import React, { useState } from 'react';
import './Style/Task.css' // Import the external CSS file

const Task = () => {
  const [showModal, setShowModal] = useState(false); // Modal visibility state

  const handleAddTaskClick = () => {
    setShowModal(true); // Show the modal
  };

  const handleCloseModal = () => {
    setShowModal(false); // Close the modal
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Perform task submission logic here
    console.log("Task Submitted!");
    setShowModal(false); // Close the modal after submission
  };

  return (
    <div className="task-page">
      <h1 className="task-title">Tasks</h1>
      <button className="add-task-button" onClick={handleAddTaskClick}>
        Add Task
      </button>

      {showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2 className="modal-title">Add Task</h2>
            <form className="task-form" onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Title:</label>
                <input type="text" required />
              </div>
              <div className="form-group">
                <label>Priority (1-5):</label>
                <input type="number" min="1" max="5" required />
              </div>
              <div className="form-group">
                <label>Start Time:</label>
                <input type="datetime-local" required />
              </div>
              <div className="form-group">
                <label>End Time:</label>
                <input type="datetime-local" required />
              </div>
              <div className="form-group">
                <label>Status:</label>
                <select required>
                  <option value="pending">Pending</option>
                  <option value="finished">Finished</option>
                </select>
              </div>
              <div className="button-group">
                <button type="submit" className="submit-button">
                  Submit
                </button>
                <button
                  type="button"
                  className="cancel-button"
                  onClick={handleCloseModal}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Task;
