import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Style/TaskList.css'; // Import the CSS file for styling

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [selectedTasks, setSelectedTasks] = useState([]);

  useEffect(() => {
    // Fetch tasks from backend
    const fetchTasks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/tasks/');
        setTasks(response.data);
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };
    fetchTasks();
  }, []);

  const handleSelectTask = (id) => {
    if (selectedTasks.includes(id)) {
      setSelectedTasks(selectedTasks.filter(taskId => taskId !== id));
    } else {
      setSelectedTasks([...selectedTasks, id]);
    }
  };

  const calculateTotalTime = (startTime, endTime) => {
    const start = new Date(startTime);
    const end = new Date(endTime);
    const totalTime = (end - start) / 1000 / 60; // Total time in minutes
    return totalTime;
  };

  const handleEditTask = (id) => {
    console.log(`Edit task with id: ${id}`);
    // Handle task edit functionality here
  };

  return (
    <div className="task-list">
      <h1>Task List</h1>
      <table className="task-table">
        <thead>
          <tr>
            <th>Select</th>
            <th>ID</th>
            <th>Title</th>
            <th>Priority</th>
            <th>Status</th>
            <th>Start Time</th>
            <th>End Time</th>
            <th>Total Time (min)</th>
            <th>Edit</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map((task) => (
            <tr key={task._id}>
              <td>
                <input
                  type="checkbox"
                  onChange={() => handleSelectTask(task._id)}
                  checked={selectedTasks.includes(task._id)}
                />
              </td>
              <td>{task._id}</td>
              <td>{task.title}</td>
              <td>{task.priority}</td>
              <td>{task.status}</td>
              <td>{new Date(task.startTime).toLocaleString()}</td>
              <td>{new Date(task.endTime).toLocaleString()}</td>
              <td>{calculateTotalTime(task.startTime, task.endTime)}</td>
              <td>
                <button onClick={() => handleEditTask(task._id)}>Edit</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TaskList;
